mapper.update:
 	update tbl_board
 	set hitno = (
	 	select ifnull(hitno,0) 
	 	from tbl_board
	 	where seqno = #{seqno}
 		) + 1
 	where seqno = #{seqno}

update tbl_board
set writer = #{writer},
	title = #{title},
	content = #{content},
	userid = #{userid}
where seqno = #{seqno}

update tbl_file
set checkfile = 'N'
where


		fileseqno = #{fileseqno}


		seqno = #{seqno}
